import React, { useEffect, useState } from 'react';
import {Table} from 'react-bootstrap';

function ExtractCsvTable(items) {
    let data = items.tableData ? items.tableData: [];
    let column = [];
    let arr = [];
    if(data.length > 0){
        column = Object.keys(data[0])
    }
    console.log(data)
    // for(let i in data){
    //     console.log(data[i].phone)
    //     if(data[i].phone === data[i+1].phone){
    //         data[i].phone = "Invalid"
    //     }
    // }
    function checkMandatoryFields(item) {
        if(item === "" || item === null){
            return "Invalid"
        }else{
            return item;
        }
    }

    useEffect(()=>{
        if(data.length > 0){
            let items = document.getElementsByClassName('unique-fields');

            var isDuplicate = data.some(function(item, idx){ 
                console.log(data.indexOf(item))
                return data.indexOf(item) != idx 
            });
            console.log(isDuplicate);

            for(let i in items){
                console.log(items[i].innerHTML);

                arr.push(items[i].innerHTML)
            }
            console.log(arr);
        }
    })

    return (
        <>
          <Table striped bordered hover style={table}>
              <thead>
                  <tr>
                {column.map((item,index)=>{
                    return(
                        <th key={index} className="table-headers">{item.split('_').join(" ")}</th>
                    )
                })}
                </tr>
              </thead>
              <tbody>
         {data.map((item,index)=>{
              return ( 
                <tr key={index}>
                <td key={index+"-username"} className="username">{checkMandatoryFields(item.username)}</td>
                <td key={index+"-fname"}>{item.first_name}</td>
                <td key={index+"-lname"}>{item.last_name}</td>
                <td key={index+"-email"} className="">{checkMandatoryFields(item.email)}</td>
                <td key={index+"-phone"} className="unique-fields">{item.phone}</td>
                </tr>
              )
            })}
            </tbody>
            </Table>
        </>
    )
}

var table = {
    "margin" : "10px"
}

export default ExtractCsvTable
