                    )
                })}
                </tr>
              </thead>
              <tbody>
         {data.map((item,index)=>{
              return ( 
                <tr key={index}>
                <td key={index+"-username"}>{checkMandatoryFields(item.username)}</td>
                <td key={index+"-fname"}>{item.first_name}</td>
                <td key={index+"-lname"}>{item.last_name}</td>
                <td key={index+"-email"}>{checkMandatoryFields(item.email)}</td>
