import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';

export default function ImportCsv(props) {

  const readExcel = (file) => {
    const promise = new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsArrayBuffer(file);

      fileReader.onload = (e) => {
        const bufferArray = e.target.result;
        console.log(bufferArray);
        const wb = XLSX.read(bufferArray, { type: "buffer" });

        const wsname = wb.SheetNames[0];

        const ws = wb.Sheets[wsname];

        const data = XLSX.utils.sheet_to_json(ws);

        resolve(data);
      };

      fileReader.onerror = (error) => {
        reject(error);
      };
    });

    promise.then((d) => {
      // console.log(d);
      console.log(d);
      for(let i in d){
        console.log(d[i].phone)
      }
      const unique = new Set();
      const showError = d.test.some(element => unique.size === unique.add(element.id).size);
      console.log(showError);

      props.tableData(d);
    });
  };

  function fileHandler(event){
    console.log(event);
    let fileObj = event.target.files[0];
    // console.log(fileObj);
    readExcel(fileObj);

  }

    return (
      <>
       <input type="file" accept=".csv,.xlsx,.xls" onChange={fileHandler.bind(this)} />
      </>
    );
  }
